function Tetsuki_Controller(board9, board13, board19, black_human, black_ai_greedy, black_ai_crawler, white_human, white_ai_greedy, white_ai_crawler, handicap, komi, beginWindow, gameHUD, gameWindow, inGameHandicap, inGameKomi, inGameBlack, inGameWhite, inGameSettings, forward_small, back_small, close_small, delete_small, more_small, pass_small, meta, goban, debug)
{
	this.board9Button = board9;
	this.board13Button = board13;
	this.board19Button = board19;
	this.blackAIGreedyButton = black_ai_greedy;
	this.blackAICrawlerButton = black_ai_crawler;
	this.blackHumanButton = black_human;
	this.whiteHumanButton = white_human;
	this.whiteAIGreedyButton = white_ai_greedy;
	this.whiteAICrawlerButton = white_ai_crawler;
	this.handicapInput  = handicap;
	this.komiInput = komi;
	this.newGameWindow = beginWindow;
	this.gameHUD = gameHUD;
	this.gameWindow = gameWindow;
	this.inGameHandicapDisplay = inGameHandicap;
	this.inGameSettingsWindow = inGameSettings;
	this.inGameKomiDisplay = inGameKomi;
	this.inGameBlack = inGameBlack;
	this.inGameWhite = inGameWhite;
	this.forwardMoveButton = forward_small;
	this.backMoveButton = back_small;
	this.closeHUDButton = close_small;
	this.deleteMoveButton = delete_small;
	this.moreButton = more_small;
	this.passMoveButton = pass_small;
	this.meta = meta;
	this.engine;
	this.handicap;
	this.komi;
	this.goban = goban;
	this.debug_output_element = debug;
}

