{
	var src="data:image/png;base64,";
	  this.closeHUDButton.setAttribute("src",src);
	  src="data:image/png;base64,";
	  this.backMoveButton.setAttribute("src",src);
	  src="data:image/png;base64,";
	  this.forwardMoveButton.setAttribute("src",src);
	  src="data:image/png;base64,";
	  this.deleteMoveButton.setAttribute("src",src);
	  src="data:image/png;base64,";
	 this.moreButton.setAttribute("src",src);
	  src="data:image/png;base64,";
	  this.passMoveButton.setAttribute("src",src);
	  this.ShowGameWindow("new_game_window");
	  delete Tetsuki_Controller.prototype.PreGame; // this function is quite big and only executed once... so we can delete the function
};

Tetsuki_Controller.prototype.SelectBoardSize = function(boardSize)
{
	this.board9Button.setAttribute('value',boardSize==9?'true':'false');
	this.board13Button.setAttribute('value',boardSize==13?'true':'false');
	this.board19Button.setAttribute('value',boardSize==19?'true':'false');
};

Tetsuki_Controller.prototype.SelectBlackPlayer = function(player)
{
	this.blackAIGreedyButton.setAttribute('value', player==="greedy" ? 'true': 'false');
	this.blackAICrawlerButton.setAttribute('value', player==="crawler" ? 'true': 'false');
	this.blackHumanButton.setAttribute('value', player==="human" ? 'true': 'false');
};

Tetsuki_Controller.prototype.SelectWhitePlayer = function(player)
{
	this.whiteAIGreedyButton.setAttribute('value', player==="greedy" ? 'true': 'false');
	this.whiteAICrawlerButton.setAttribute('value', player==="crawler" ? 'true': 'false');
	this.whiteHumanButton.setAttribute('value', player==="human" ? 'true': 'false');
};

Tetsuki_Controller.prototype.StartGame = function()
{
	  this.ShowGameWindow("game_window");
	
	if(this.board9Button.attributes["value"].nodeValue==="true")
	{
		this.engine = new Game(9);
	}
	else if(this.board13Button.attributes["value"].nodeValue==="true")
	{
		this.engine = new Game(13);
	}
	else if(this.board19Button.attributes["value"].nodeValue==="true")
	{
		this.engine = new Game(19);
	}
	else
	{
		this.engine = new Game(19);
	}
	this.PlayLoop();
};

Tetsuki_Controller.prototype.PlayLoop = function()
{
	var isHumanTurn;
	if((this.engine.current_board.side === BLACK && this.blackHumanButton.attributes["value"].nodeValue==="true") || (this.engine.current_board.side === WHITE && this.whiteHumanButton.attributes["value"].nodeValue==="true"))
	{
		isHumanTurn = true;
	}
	else
	{
		isHumanTurn = false;
	}
	
	while(!isHumanTurn)
	{
		//generate and make ai move for current color
		var move = this.engine.Generate_Move();
		this.engine.Make_Move(move);
		this.SetStone(move,this.engine.current_board.side);
		//end the game condition
		if(this.engine.move_history.length()>=2 && this.engine.move_history[-1]==PASS_MOVE && this.engine.move_history[-2]==PASS_MOVE)
		{
			break;
		}
		
		//wait for human turn condition
		if((this.engine.current_board.side === BLACK && this.blackHumanButton.attributes["value"].nodeValue==="true") || (this.engine.current_board.side === WHITE && this.whiteHumanButton.attributes["value"].nodeValue==="true"))
		{
			isHumanTurn = true;
		}
	}
};

Tetsuki_Controller.prototype.BackToGame = function()
{
	
};

Tetsuki_Controller.prototype.Tap = function(coord_x,coord_y)
{
	var isIllegal = this.engine.Make_Move([coord_x,coord_y]);
	if (isIllegal)
	{
		this.ShowGameHUD(true, coord_x,coord_y);
	}
	else
	{
		//update the ui goban with move
		this.SetStone([coord_x,coord_y],this.engine.current_board.side);
		if (this.engine.current_board.side === BLACK)
		{
			this.engine.current_board.side = WHITE;
		}
		else
		{
			this.engine.current_board.side = BLACK;
		}
	}

	this.PlayLoop();
};

Tetsuki_Controller.prototype.SetStone = function(move,color)
{
//  debug("settings stone")
	var cell=this.goban.rows[move[1]].cells[move[0]];
	if ("X"==color)
    cell.innerHTML="<div class='black'></div>";
	else if ("O"==color)
    cell.innerHTML="<div class='white'></div>";
	else if ("."==color)
    cell.innerHTML="";
	
}


Tetsuki_Controller.prototype.ShowGameHUD = function(isVisible)
{
	
};

Tetsuki_Controller.prototype.ShowGameWindow = function(window_name)
{	
	this.ShowHUDWindow(false,0,0);
	this.newGameWindow.visible=false;
	this.gameWindow.visible=false;
	this.inGameSettingsWindow.visible=false;
	this.newGameWindow.setAttribute("visible", (window_name=="new_game_window")?"inline":"no");
	this.gameWindow.setAttribute("visible",(window_name==="game_window")?"yes":"no");
	this.inGameSettingsWindow.setAttribute("visible",(window_name=="settings_window")?"inline":"no");
	this.meta.setAttribute("content", (window_name=="game_window")? "initial-scale=1.0, width=969, maximum-scale=1.0, minimum-scale=0.3": "initial-scale=1.0, width=320, minimum-scale=1.0, maximum-scale=1.0");

	if (window_name=="settings_window")
	{
		this.inGameBlack.setAttribute("value", currentGame.player[0]);
		this.inGameWhite.setAttribute("value", currentGame.player[1]);
		this.inGameHandicap.setAttribute("value", currentGame.handicap);
		this.inGameKomi.setAttribute("value", currentGame.komi);
	}
	setTimeout(function(){window.scrollTo(0, 1);}, 1000);
};

Tetsuki_Controller.prototype.ShowHUDWindow = function(isVisible,x,y)
{
	this.gameHUD.setAttribute( "visible", (isVisible?"yes":"no"));
	if (isVisible)
	{
		var pixx=(51*x)+4;
		var maxx = currentGame.boardSize * 51 - 294;
		if (pixx > maxx)
		{
			 pixx = maxx;
		}
		this.gameHUD.setAttribute( "style", "top: " + ((51*y)+4)+"px; left: "+pixx+"px;");
	}
};

